// checkAge();
function checkAge() {
    const myAge = 32;
    let message = '';

    if (myAge < 18) {
        message = `Sei troppo giovane! Hai ${myAge} anni!`;
    } else {
        message = 'Hai più di 18 anni!';
    }
}
checkAge();

// 1- in base al valore (se maggiore o minore di 18) di "myAge", stamperà un messaggio diverso
// 3- "message" non potrà variare se viene definito come una costante

