// addNumbers();
function addNumbers() {
    const userNumber = prompt('Inserisci un numero');
    const total = parseInt(userNumber) + 12;

    console.log(`Il risultato finale è ${total}`);
}
addNumbers();
// 1- stampare il numero inserito dall'utente + 12
// 2- "userNumber" risulta come stringa


